import gql from 'graphql-tag'

const FETCH_POSTS_QUERY = gql`
  query posts(
    $limit: Int,
    $offset: Int
  ){
    posts(pagination:{
      limit: $limit,
      offset: $offset
    }) {
      id title createdAt image
    }
  }
`

export { FETCH_POSTS_QUERY };
