import Link from 'next/link'
import { useContext, useState } from 'react'
import { useRouter } from 'next/router'
import UserContext from '../components/UserContext'
import styles from './Register.module.css'

import { useMutation } from '@apollo/react-hooks'
import gql from 'graphql-tag'

function Register() {
  const router = useRouter();
  const { toggleLogin } = useContext(UserContext);

  const [values, setValues] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  })

  const onChange = (event) => {
    setValues({...values, [event.target.name]: event.target.value});
  }

  const [addUser, { loading }] = useMutation(REGISTER_USER, {
    update(proxy, result){
      console.log(result)
    },
    variables: {
      email: values.email,
      password: values.password
    }
  })

  const onSubmit = (event) => {
    event.preventDefault();
    addUser();
  }

  

  const handleRegister = (e) => {
    toggleLogin(true);
    e.preventDefault();
    router.push('/');
  }

  return (
    <div className={styles.register__container}>
      <div className={styles.container__content}>
        <div className={styles.content__title}>
          <h2 className={styles.title__text}>Register</h2>
        </div>
        <form className={styles.content__input}>
          <div className={styles.input__login}>
            <p className={styles.login__text}>Email</p>
            <input 
              type="email"
              name="email"
              className={styles.login__textbox}
              value={values.email}
              onChange={onChange}
            />
          </div>
          <div className={styles.input__login}>
            <p className={styles.login__text}>Password</p>
            <input 
              type="password" 
              name="password"
              className={styles.login__textbox}
              value={values.password}
              onChange={onChange}
            />
          </div>
          <div className={styles.input__login}>
            <p className={styles.login__text}>Confirm Password</p>
            <input 
              type="password"
              name="confirmPassword" 
              className={styles.login__textbox}
              value={values.confirmPassword}
              onChange={onChange}  
            />
          </div>
          <button 
            className={styles.input__button}
            onClick={handleRegister}
          >
            Register
          </button>
          <p className={styles.input__register}>
            Already have an account?
            <Link href="/login">
              <a className={styles.register__link}>Login Here</a>
            </Link>
          </p>
        </form>
      </div>
    </div>
  )
}

const REGISTER_USER = gql`
  mutation Register(
    $email: String!
    $password: String!
  ) {
    register(
      email: $email
      password: $password
    ) 
  }
`

export default Register;
