import Link from 'next/link'
import useRouter from 'next/router'
import { useEffect, useState, useContext } from 'react'
import UserContext from '../components/UserContext'
import styles from './navbar.module.css'

function renderButton(userStatus, currentPath, setLogin) {
  if(!userStatus) {
    if(currentPath == "/") {
      return (
        <Link href="/login">
          <a className={styles.container__text}>LOGIN</a>
        </Link>
      );
    } else {
      return (
        <Link href="/">
          <a className={styles.container__text}>CLOSE</a>
        </Link>
      );
    }
  } else {
    return (
      <p 
        className={styles.container__text}
      >
        LOGOUT
      </p>
    )
  }
}

export default function Navbar() {

  const { loginStatus, toggleLogin } = useContext(UserContext);
  const [currentPath, updateCurrentPath] = useState();

  useEffect(() => {
    updateCurrentPath(useRouter.router.pathname);
  }, [])

  return (
    <div className={styles.nav__container}>
      <img src="/images/LOGO.svg" alt="Logo" />
      {renderButton(loginStatus, currentPath, toggleLogin)}
    </div>
  )
}
