import axios from 'axios';
import Link from 'next/link'
import { useEffect, useState } from 'react'
import Navbar from '../../components/navbar';
import Footer from '../../components/footer';
import styles from './viewpost.module.css';

export default function ViewPost() {

  const [postDetails, updatePostDetails] = useState();
  const [newComment, updateNewComment] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3000/post-details.json')
    .then((res) => {
      updatePostDetails(res.data);
    })
  },[]);

  const handleChange = (event) => {
    updateNewComment(event.target.value);
  }

  const handleSubmit = () => {
    let tempPost = postDetails;
    let tempObject = {
      comment_item: "",
      comment_time: ""
    };

    tempObject.comment_item = newComment;
    tempObject.comment_time = "JUST NOW!!!";
    tempPost.comments.unshift(tempObject);

    updatePostDetails(tempPost);
    updateNewComment("");
  }

  return (
    <div className="container">
      <Navbar />
      <div className={styles.navigation__header}>
        <div className={styles.navigation__content}>
          <Link href="/">
            <a className={styles.content__text}>HOME</a>
          </Link>
          <p>&nbsp;&nbsp;{' > '}&nbsp;</p>
          <p>&nbsp;サンプルテキストサンプル ルテキストサンプルテキストサンプルテキストサンプル ルテキスト</p>
        </div>
      </div>
      <div className={styles.navigation__body}>
        <div className={styles.body__action}>
          <p className={styles.action__text}>Edit Post</p>
        </div>
        { postDetails ?
          <div className={styles.body__details}>
            <p className={styles.details__date}>{postDetails.date}</p>
            <h2 className={styles.details__title}>{postDetails.title}</h2>
            <img
              className={styles.details__image} 
              src="/images/girl.png"
            />
            <p className={styles.details__body}>{postDetails.body}</p>
          </div>
          : null
        }
        <hr />
        { postDetails ?
          <div className={styles.body__comments}>
            <h2 className={styles.comments__title}>COMMENT</h2>
            {postDetails.comments.map((element, index) => {
              return(
                <div key={index} className={styles.comments__item}>
                  <p className={styles.item__content}>{element.comment_item}</p>
                  <p className={styles.item__date}>{element.comment_time}</p>
                </div>
              ); 
            })}
            <div className={styles.comments__input}>
              <textarea
                placeholder="Write comment" 
                value={newComment}
                onChange={handleChange}
                className={styles.input__textarea} 
              />
              <input 
                type="button" 
                value="Submit"
                onClick={handleSubmit}
                className={styles.input__submit}
              />
            </div>
          </div>
          : null
        }
      </div>
      <Footer /> 
    </div>
  )
}
