import axios from 'axios';
import Link from 'next/link'
import { useEffect, useState, useRef } from 'react'
import { useContext } from 'react'
import UserContext from '../../components/UserContext'
import Navbar from '../../components/navbar';
import Footer from '../../components/footer';
import styles from './viewpost.module.css';

import { useQuery } from '@apollo/react-hooks'
import gql from 'graphql-tag'

import { useRouter } from 'next/router'



function ViewPost() {
  const router = useRouter();
  const { postId } = router.query;
  console.log(postId);

  const { loading, data } = useQuery(FETCH_POST_QUERY, {
    variables: { postId }
  })

  const { isLogin } = useContext(UserContext);

  const [newComment, updateNewComment] = useState('');
  const [isEditing, setEditStatus] = useState(false);
  const [postDate, setPostDate] = useState('');
  const [postTitle, setPostTitle] = useState('');
  const [postImage, setPostImage] = useState('');
  const [postBody, setPostBody] = useState('');
  const [postComments, setPostComments] = useState([]);
  const [tempTitle, setTempTitle] = useState('');
  const [tempBody, setTempBody] = useState('');
  const [tempImage, setTempImage] = useState('');

  const uploadImage = useRef(null);
  
  useEffect(() => {
    console.log(data);
    // axios.get('http://localhost:3000/post-details.json')
    // .then((res) => {
    //   setPostDate(res.data.date)
    //   setPostTitle(res.data.title);
    //   setPostBody(res.data.body);
    //   setPostImage(res.data.image_path);
    //   setPostComments(res.data.comments);

    //   setTempTitle(res.data.title);
    //   setTempBody(res.data.body);
    //   setTempImage(res.data.image_path);
    // })
  },[]);

  const cancelEditPost = () => {
    setTempTitle(postTitle);
    setTempBody(postBody);
    setTempImage(postImage);
    setEditStatus(!isEditing);
  }

  const saveEditPost = () => {
    setPostTitle(tempTitle);
    setPostBody(tempBody);
    setPostImage(tempImage);
    setEditStatus(!isEditing);
  }

  const handleComment = (event) => {
    updateNewComment(event.target.value);
  }

  const handleTitle = (event) => {
    setTempTitle(event.target.value);
  }

  const handleImageUpload = (event) => {
    uploadImage.current.click();
  }

  const handleImage = (event) => {
    if(event.target.files[0]){
      setTempImage(URL.createObjectURL(event.target.files[0]));
    }
  }

  const handleBody = (event) => {
    setTempBody(event.target.value);
  }

  const handleSubmit = () => {
    let tempArray = postComments;
    let tempObj = {
      comment_item: "",
      comment_time: ""
    }

    tempObj.comment_item = newComment;
    tempObj.comment_time = "A moment ago";
    tempArray.unshift(tempObj);

    setPostComments(tempArray)
    updateNewComment("");
  }

  return (
    <div className="container">
      <div className={styles.viewpost__container}>
        <Navbar />
        { postTitle ?
          <div className={styles.navigation__header}>
            <div className={styles.navigation__content}>
              <Link href="/">
                <a className={styles.content__text}>HOME</a>
              </Link>
              <p>&nbsp;&nbsp;{' > '}&nbsp;</p>
              <p>&nbsp;{postTitle}</p>
            </div>
          </div>
          : null
        }
        <div className={styles.navigation__body}>
          { isLogin ?
            <div className={styles.body__action}>
              { isEditing ?
              <div className={styles.action__container}>
                <p 
                  className={styles.action__text}
                  onClick={saveEditPost}
                >
                  Save Post</p>
                <p 
                  className={styles.action__text}
                  onClick={cancelEditPost}
                >Cancel</p>
              </div>
              : <p 
                  className={styles.action__text}
                  onClick={() => setEditStatus(!isEditing)}
                >Edit Post</p>
              }
            </div>
            : null
          }
          { postTitle || postBody || postImage ?
            <div className={styles.body__details}>
              <p className={styles.details__date}>{postDate}</p>
              { !isEditing 
              ? <h2 className={styles.details__title}>{postTitle}</h2>
              : <textarea 
                  defaultValue={tempTitle}
                  onChange={handleTitle}
                  className={styles.details__title}
                />
              }
              <div className={styles.details__container}>
                { isEditing 
                  ? 
                  <div className={styles.details__}>
                    <button
                      className={styles.details__upload_image}
                      onClick={handleImageUpload}
                    >
                      Upload Image
                    </button>
                    <input 
                      name="myfile" 
                      type="file"
                      ref={uploadImage} 
                      onChange={handleImage}
                      hidden
                    />
                  </div>
                  : null
                }
                <img
                  className={styles.details__image} 
                  src={tempImage}
                />
              </div>
              { !isEditing
                ? <p className={styles.details__body}>{postBody}</p>
                : <textarea
                    defaultValue={tempBody}
                    onChange={handleBody}
                    className={styles.details__body}
                  />
              }
            </div>
            : null
          }
          <hr />
          { postComments ?
            <div className={styles.body__comments}>
              <h2 className={styles.comments__title}>COMMENT</h2>
              {postComments.map((element, index) => {
                return(
                  <div key={index} className={styles.comments__item}>
                    <p className={styles.item__content}>{element.comment_item}</p>
                    <p className={styles.item__date}>{element.comment_time}</p>
                  </div>
                ); 
              })}
              <div className={styles.comments__input}>
                <textarea
                  placeholder="Write comment" 
                  value={newComment}
                  onChange={handleComment}
                  className={styles.input__textarea} 
                />
                <input 
                  type="button" 
                  value="Submit"
                  onClick={handleSubmit}
                  className={styles.input__submit}
                />
              </div>
            </div>
            : null
          }
        </div>
      </div>
      <Footer /> 
    </div>
  )
}

export const getServerSideProps = async ({params}) => {
  return {props: {}};
}

const FETCH_POST_QUERY = gql`
  {
    post(id: $postId) {
      id title content
    }
  }
`


export default ViewPost;
