import Link from 'next/link'
import Card from './posts/postcard'
import styles from './news.module.css'
import { useEffect, useState, useContext } from 'react'
import UserContext from '../components/UserContext'
import axios from 'axios'

import { useQuery } from '@apollo/react-hooks'
import gql from 'graphql-tag'


function News(props) {

  const { loading, data } = useQuery(FETCH_POSTS_QUERY);

  console.log(data);

  const { loginStatus, toggleLogin } = useContext(UserContext);
  const [feedLimit, setFeedLimit] = useState(6);

  // const [cardPost, updateCardPosts] = useState([]);

  // useEffect(() => {
  //   axios.get('http://localhost:3000/news-posts.json')
  //   .then((res) => {
  //     updateCardPosts(res.data);
  //   })
  // },[]);

  function updateFeedLimit() {
    setFeedLimit(feedLimit * 2);
  }


  return (
    <div className={styles.news__container}>
      <div className={styles.container__title}>
        <h2 className={styles.title__text}>News</h2>
        { loginStatus ?
          <Link href="/posts/createpost">
            <a className={styles.title__newpost}>
              Create New Post
            </a>
          </Link>
          : null
        }
      </div>
      { data && 
        <div className={styles.container__content}>
          {
            data.posts.filter((item, index) => (index < feedLimit))
            .map((filteredItem) => 
              (
                <Link href={`/posts/viewpost/:${filteredItem.id}`} key = {filteredItem.id}>
                  <a>
                    <Card 
                      date = {filteredItem.createdAt}
                      description = {filteredItem.content}
                    />
                  </a>
                </Link>
              )
            )
          }
        </div>
      }
      {
        (data && (data.posts.length > feedLimit)) 
        ? <div className={styles.container__button__wrapper}>
            <button 
              className={styles.container__button}
              onClick={updateFeedLimit}
            >
              Load More
            </button>
          </div>
        : null
      }
    </div>
  )
}

const FETCH_POSTS_QUERY = gql`
  {
    posts {
      id content createdAt
    }
  }
`

export default News;
