import { useEffect, useState } from 'react'
import styles from './slideshow.module.css'
import axios from 'axios'

export default function SlideShow() {

  const [showSlide, updateSlide] = useState(false);
  const [slideImages, updateImages] = useState([]);
  const [currentIndex, updateCurrentIndex] = useState(0);
  
  useEffect(() => {
    axios.get('http://localhost:3000/images-list.json')
    .then((res) => {
      console.log(res.data);
      updateImages(res.data);
      updateSlide(true);
    })
  }, [])

  const nextImage = () => {
    if(currentIndex != 0 && Math.abs(currentIndex + 1) % slideImages.length == 0){
      updateCurrentIndex(0);
    } else {
      updateCurrentIndex(currentIndex + 1);
    }
  }

  const prevImage = () => {
    if(currentIndex == 0){
      updateCurrentIndex(slideImages.length-1);
    } else {
      updateCurrentIndex(currentIndex - 1);
    }
  }

  const changePage = (selectedPage) => {
    updateCurrentIndex(selectedPage);
  }

  return (
    <div className={styles.slideshow__container}>
      { showSlide
        ? <div className={styles.slideshow__actions}>
            <img 
              src="images/left.svg" 
              alt="left arrow" 
              className={styles.actions__arrows}
              onClick={prevImage}
            />
            <div className={styles.actions__selector}>
              {
                slideImages.map((item, index) => {
                    if(index == currentIndex) {
                      return <img src="images/CircleDark.svg" />
                    } else {
                      return <img src="images/CircleLight.svg" onClick={() => changePage(index)}/>
                    }
                })
              }
              {/* <img 
                src="images/CircleDark.svg" 
                alt="Selected Slide"
              />
              <img 
                src="images/CircleLight.svg" 
                alt="Select Slide" 
              />
              <img 
                src="images/CircleLight.svg" 
                alt="Select Slide" 
              /> */}
            </div>
            <img 
              src="images/right.svg" 
              alt="right arrow" 
              className={styles.actions__arrows}
              onClick={nextImage}
            />
          </div>
        : null
      }
      <div className={styles.slideshow__image_wrapper}>
        { showSlide
        ? <img 
            src={slideImages[currentIndex].image_path} 
            className={styles.slideshow__image}
          />
        : null
        }  
      </div>
    </div>
  )
}
