import Link from 'next/link'
import { useRouter } from 'next/router'
import { useEffect, useState, useContext } from 'react'
import styles from './navbar.module.css'

function renderButton(userStatus, currentPath) {

  const router = useRouter();

  const handleLogOut = (e) => {
    toggleLogin(false);
    e.preventDefault();
    router.push('/')
  }

  if(!userStatus) {
    if(currentPath == "/") {
      return (
        <Link href="/login">
          <a className={styles.container__text}>LOGIN</a>
        </Link>
      );
    } else {
      return (
        <Link href="/">
          <a className={styles.container__text}>CLOSE</a>
        </Link>
      );
    }
  } else {
    return (
      <p 
        className={styles.container__text}
        onClick={handleLogOut}
      >
        LOGOUT
      </p>
    )
  }
}

export default function Navbar() {

  const router = useRouter();
  const [loginStatus, setLoginStatus] = userStatus(false);
  const [currentPath, updateCurrentPath] = useState();

  useEffect(() => {
    if(localStorage.getItem('token')){
      setLoginStatus(true);
    }
    updateCurrentPath(router.pathname);
  }, [])

  return (
    <div className={styles.nav__container}>
      <img src="/images/LOGO.svg" alt="Logo" />
      {renderButton(loginStatus, currentPath)}
    </div>
  )
}
